mapper.update:
UPDATE kid SET 
	license_kindergarten = #{licenseKindergarten}
	, kid_name = #{kidName}
	, kid_birth = #{kidBirth}
	, kid_address = #{kidAddress}
	, kid_protector_phone = #{kidProtectorPhone}
	, kid_protector_phone_reserve = #{kidProtectorPhoneReserve}
	, kid_precautions = #{kidPrecautions} 
	, kid_gender = #{kidGender}
	, kid_commuting = #{kidCommuting}
	, kid_entrance_day = DATE_FORMAT(NOW(),'%Y-%m-%d')
WHERE  kid_no = #{kidNo}

mapper.select:
SELECT
license_kindergarten , kid_name , kid_birth , kid_address , kid_protector_phone , kid_protector_phone_reserve , kid_precautions , kid_gender , kid_commuting , kid_entrance_day
FROM kid WHERE kid_no = #{kidNo}
