mapper.insert:
	INSERT INTO education_project_form(
  		form_order
  		,license_kindergarten
  		,form_val
  		,form_merge
  		,form_borders
  		,form_count_row
  		,form_count_col
  		,form_title
  	)VALUES(
  		(
  			SELECT * FROM
	  		(SELECT COUNT(form_no)
	  		FROM education_project_form
	  		WHERE license_kindergarten = #{licenseKindergarten}) 
	  		as temp
  		) + 1
  		,#{licenseKindergarten}
  		,#{formVal}
  		,#{formMerge}
  		,#{formBorders}
  		,#{formCountRow}
  		,#{formCountCol}
  		,#{formTitle}
  	)

mapper.select:
SELECT e.education_project_no AS educationProjectNo ,e.val ,e.merge ,e.borders ,e.count_row AS countRow ,e.count_col AS countCol ,e.add_date AS addDate ,c.category_name AS categoryNo ,e.age ,e.class_no AS classNo FROM education_project e INNER JOIN education_category c ON e.category_no = c.category_no WHERE license_kindergarten = #{licenseKindergarten}
AND age = #{age}
AND e.class_no = ${classNo}
AND e.category_no = #{categoryNo} AND e.project_date_info = #{projectDateInfo}

SELECT e.education_project_no AS educationProjectNo ,e.add_date AS addDate ,e.category_no AS categoryNo ,age
,e.class_no AS classNo
,e.project_date_info AS projectDateInfo FROM education_project e
INNER JOIN kid_class k ON e.class_no = k.class_no
WHERE e.license_kindergarten = #{licenseKindergarten} AND e.category_no = ${categoryNo}
AND e.class_no = #{classNo}
AND e.age = #{age}
ORDER BY e.project_date_info DESC
