mapper.select:
SELECT d.detailes_no AS detailesNo ,d.development_title AS developmentTitle ,c.class_name AS className ,k.kid_no AS kidNo FROM development_details d INNER JOIN development dev ON dev.development_no = d.development_no INNER JOIN kid_formation f ON f.kid_formation_no = dev.kid_formation_no INNER JOIN kid_class c ON c.class_no = f.class_no INNER JOIN kid k ON k.kid_no = f.kid_no WHERE d.license_kindergarten = #{licenseKindergarten}
AND c.class_age = #{searchAge}
AND k.kid_name LIKE CONCAT('%',#{searchVal},'%')
AND c.class_name LIKE CONCAT('%',#{searchVal},'%')
AND k.kid_name LIKE CONCAT('%',#{searchVal},'%') OR c.class_name LIKE CONCAT('%',#{searchVal},'%') OR d.development_title LIKE CONCAT('%',#{searchVal},'%')
ORDER by k.kid_name ASC LIMIT #{startRow}, #{onePageRow};

SELECT opserve_content
FROM development_opserve
WHERE opserve_no = #{opserveNo}

SELECT f.kid_formation_no AS KidFormationNo ,k.kid_name AS kidName ,k.kid_birth AS kidBirth ,k.kid_gender AS kidGender ,c.class_name AS className ,c.class_age AS classAge FROM kid_formation f INNER JOIN kid k ON k.kid_no = f.kid_no INNER JOIN kid_class c ON c.class_no = f.class_no WHERE c.class_year = #{year} AND c.license_kindergarten = #{licenseKindergarten}
AND c.class_age = #{searchAge}
AND k.kid_name LIKE CONCAT('%',#{searchVal},'%')
AND c.class_name LIKE CONCAT('%',#{searchVal},'%')
AND k.kid_name LIKE CONCAT('%',#{searchVal},'%') OR c.class_name LIKE CONCAT('%',#{searchVal},'%')
ORDER by k.kid_name ASC LIMIT #{startRow}, #{onePageRow};

mapper.update:
UPDATE kid SET 
	license_kindergarten = #{licenseKindergarten}
	, kid_name = #{kidName}
	, kid_birth = #{kidBirth}
	, kid_address = #{kidAddress}
	, kid_protector_phone = #{kidProtectorPhone}
	, kid_protector_phone_reserve = #{kidProtectorPhoneReserve}
	, kid_precautions = #{kidPrecautions} 
	, kid_gender = #{kidGender}
	, kid_commuting = #{kidCommuting}
	, kid_entrance_day = DATE_FORMAT(NOW(),'%Y-%m-%d')
WHERE  kid_no = #{kidNo}
