mapper.delete:
DELETE FROM 
	equipment_request 
WHERE 
	request_no=#{requestNo}
AND 
	license_kindergarten=#{licenseKindergarten};

mapper.insert:
INSERT INTO equipment(
	  license_kindergarten
	, equipment_name
	, teacher_no
	, category_no
	, equipment_cost
	, equipment_amount
	, equipment_customer
	, equipment_state
	, equipment_day
) VALUES(
	 #{licenseKindergarten}
	,#{equipmentName}
	,#{teacherNo}
	,#{categoryNo}
	,#{equipmentCost}
	,#{equipmentAmount}
	,#{equipmentCustomer}
	,#{equipmentState}
	,now()
);
