mapper.select:
SELECT COUNT(c.equipment_no) FROM(
	SELECT * FROM equipment
	Where license_kindergarten=#{licenseKindergarten}
	GROUP BY equipment_name) as c;

mapper.insert:
INSERT INTO equipment(
	  license_kindergarten
	, equipment_name
	, teacher_no
	, category_no
	, equipment_cost
	, equipment_amount
	, equipment_customer
	, equipment_state
	, equipment_day
) VALUES(
	 #{licenseKindergarten}
	,#{equipmentName}
	,#{teacherNo}
	,#{categoryNo}
	,#{equipmentCost}
	,#{equipmentAmount}
	,#{equipmentCustomer}
	,#{equipmentState}
	,now()
);
